lass="checker" ><span class="checked"><input type="checkbox" value="1" id="selct'+i+'" name="selct'+i+'" checked="checked"  onClick="checkun('+i+');" onMouseMove="getAction();" ></span></div>';                    }             }     } else {         for (var i = 1; i <= max; i++) {		    if(document.getElementById("choix"+i)){             document.getElementById("choix"+i).innerHTML='<div class="checker"><span ><input type="checkbox" value="1" id="selct'+i+'" name="selct'+i+'"   onClick="checkun('+i+');"  onMouseMove="getAction();"  ></span></div>';                 }}     } } function checkun(ele) {var inp=document.getElementById("selct"+ele);     if (inp.checked) {	        document.getElementById("choix"+ele).innerHTML='<div class="checker"><span class="checked"><input type="checkbox" checked value="1" id="selct'+ele+'" name="selct'+ele+'"  onClick="checkun('+ele+');"  onMouseMove="getAction();"   ></span></div>';     } else {        document.getElementById("choix"+ele).innerHTML='<div class="checker" ><span ><input type="checkbox" value="1"  id="selct'+ele+'"  name="selct'+ele+'"   onClick="checkun('+ele+');"  onMouseMove="getAction();" ></span></div>';                       } } function number_format(number, decimals, dec_point, thousands_sep) {    number = (number + '')    .replace(/[^0-9+\-Ee.]/g, '');  var n = !isFinite(+number) ? 0 : +number,    prec = !isFinite(+decimals) ? 0 : Math.abs(decimals),    sep = (typeof thousands_sep === 'undefined') ? ',' : thousands_sep,    dec = (typeof dec_point === 'undefined') ? '.' : dec_point,    s = '',    toFixedFix = function(n, prec) {      var k = Math.pow(10, prec);      return '' + (Math.round(n * k) / k)        .toFixed(prec);    };  // Fix for IE parseFloat(0.55).toFixed(0) = 0;  s = (prec ? toFixedFix(n, prec) : '' + Math.round(n))    .split('.');  if (s[0].length > 3) {    s[0] = s[0].replace(/\B(?=(?:\d{3})+(?!\d))/g, sep);  }  if ((s[1] || '')    .length < prec) {    s[1] = s[1] || '';    s[1] += new Array(prec - s[1].length + 1)      .join('0');  }  return s.join(dec);}  			function TotallCheck(){	max_TAX=document.getElementById("id_Tax").value;				max=document.getElementById("max_CP").value;		for(j=1;j<=max_TAX;j++){	MN=document.getElementById("TX"+j).value;		$tot_com=1*0;	$tot_fct=0*1;	$tots=0;	for(i=1;i<=max;i++){				if(document.getElementById("V"+MN+i)){		valeur=document.getElementById("V"+MN+i);	remember=document.getElementById("selct"+i);		if(remember.checked == 1 ){	var ch3=new Number(valeur.value);		var ch4=new Number($tot_com); 	$tot_com=Number(ch3+ch4);			}		}			}				tot=document.getElementById("total");	fct=document.getElementById("fct"+MN);	com=document.getElementById("cmd"+MN);	tot1=document.getElementById("tt"+MN);	RST=document.getElementById("Rest"+MN);	TF=document.getElementById("ttCom"+MN);	TC=document.getElementById("ttFct"+MN);		tot=Number($tot_com);			tot1.innerHTML=number_format(tot, 2, ',', '.');		RST.value=number_format(tot, 2, '.', '');					}				}		function getVirement(){var max=document.getElementById("max_per").value;test_pay=0;for(i=1;i<=max;i++){remember=document.getElementById('Choix_per'+i);	if(remember.checked == 1 ){		test_pay=1;	}	}if(test_pay=="1"){document.getElementById('fiche_paiment').innerHTML='<button type="submit"  class="btn green"><?php echo $N28; ?></button>';}else{document.getElementById('fiche_paiment').innerHTML='';}		}	function getAction(){var max=document.getElementById("max_CP").value;test_pay=0;for(i=1;i<=max;i++){remember=document.getElementById('selct'+i);	if(remember.checked == 1 ){		test_pay=1;	}	}if(test_pay=="1"){document.getElementById('go').innerHTML='<button type="submit"  class="btn green"><?php echo $N19; ?></button>';}else{document.getElementById('go').innerHTML='';}		}	function getValDevise(from, to) {    var script = document.createElement('script');    script.setAttribute('src', "http://query.yahooapis.com/v1/public/yql?q=select%20rate%2Cname%20from%20csv%20where%20url%3D'http%3A%2F%2Fdownload.finance.yahoo.com%2Fd%2Fquotes%3Fs%3D"+from+to+"%253DX%26f%3Dl1n'%20and%20columns%3D'rate%2Cname'&format=json&callback=parseExchangeRate");    document.body.appendChild(script);  }  var ident; function parseExchangeRate(data) {    var name = data.query.results.row.name;    var rate = parseFloat(data.query.results.row.rate, 10);		document.getElementById(ident).value=rate;     }  function getDevise(orig,dest,spaceaff){ mo=dest.value;  ident=spaceaff.id; res = mo.split("//Sep");  getValDevise(orig,res[1]);   }  </script></head><div class="portlet-body">											<div class="tabbable">									<ul class="nav nav-tabs">										<li class="<?php if(!isset($_GET['smtp_envoi'])){ echo 'active';} ?>">											<a href="#tab_general" data-toggle="tab">											<?php echo $N1; ?> </a>										</li>																													<li>											<a href="#tab_reviews" data-toggle="tab">											<?php echo $N3; ?> 											</a>										</li>																													</ul>									</div>	  				<?php?> <div class="tab-content no-space">	       <div class="tab-pane active" id="tab_general">		   <form  action="MenuUtilisation.php?valeur=payee_Salaire.php&url=<?php echo $_GET['url'] ?>&titre=<?php echo $_GET['titre'] ?>" method="POST">		<!-- début -->		<?php 		 $s=mysql_query(" select CIN,Salaire,Mois,annee ,Monnaie from salaried where  pay='0'");?>						<div class="portlet-title">														<div class="tools">							</div>						</div>						<div class="portlet-body" >		<table class="table table-striped table-bordered table-hover" id="sample_6" >		<thead>				<tr>     				<th><font size="1">#</th>     				<th><font size="1"><?php echo $N4; ?></th> 					<th><font size="1"><?php echo $N6; ?></th> 					<th><font size="1"><?php echo $N8; ?><